mapper.select:
select P.PRODUCT_NUMBER, P.NAME, P.DESCRIPTION, P.PRICE, P.QUANTITY, P.CREATED_DATE from PRODUCT_TB P WHERE 1=1
AND (P.PRODUCT_NUMBER LIKE '%' || #{searchInput} || '%' OR P.NAME LIKE '%' || #{searchInput} || '%')
AND P.QUANTITY >= #{stock_min}
AND P.QUANTITY <= #{stock_max}
AND P.PRICE >= #{price_min}
AND P.PRICE <= #{price_max}
AND P.CREATED_DATE >= TO_DATE(#{fr_date}, 'YYYY-MM-DD')
AND P.CREATED_DATE <= TO_DATE(#{to_date}, 'YYYY-MM-DD')
AND P.PRODUCT_NUMBER IN ( SELECT M.PRODUCT_NUMBER FROM PRODUCT_CATEGORY_MAPPING_TB M JOIN PRODUCT_CATEGORY_TB C ON M.CATEGORY_ID = C.ID WHERE C.ID = #{category} )
AND P.PRODUCT_NUMBER IN ( SELECT S.PRODUCT_NUMBER FROM SALES_POST_TB S JOIN PRODUCT_TB P ON P.PRODUCT_NUMBER = S.PRODUCT_NUMBER WHERE S.STATUS = #{sale_status} )

SELECT id, name, category_code AS categoryCode, next_val
FROM PRODUCT_CATEGORY_TB
WHERE id = #{id}

SELECT PRODUCT_NUMBER, NAME, DESCRIPTION, PRICE, QUANTITY, CREATED_DATE
FROM PRODUCT_TB
WHERE PRODUCT_NUMBER = #{productNumber}
