mapper.update:
UPDATE PRODUCT_TB 
SET QUANTITY = QUANTITY - #{quantity} 
WHERE PRODUCT_NUMBER = #{productNumber}

mapper.delete:
DELETE FROM CART_TB 
WHERE CUSTOMER_ID = #{identifierId} 
  AND PRODUCT_NUMBER = #{productNumber}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM ORDER_TB 
WHERE ORDER_NUMBER = #{orderNumber}

mapper.update:
UPDATE PRODUCT_TB
SET QUANTITY = QUANTITY + #{quantity}
WHERE PRODUCT_NUMBER = #{productNumber}
