mapper.select:
SELECT *
FROM PRODUCT_NUTRITION_TB


        PROTEIN >= #{protein}


        AND TRANS_FAT <= #{transFat}


        AND SATURATED_FAT <= #{saturatedFat}


        AND SUGAR <= #{sugar}


        AND SODIUM <= #{sodium}


        AND CARBOHYDRATE >= #{carbohydrate}

SELECT PRODUCT_NUMBER, NAME, DESCRIPTION, PRICE, QUANTITY, CREATED_DATE
FROM PRODUCT_TB
WHERE PRODUCT_NUMBER = #{productNumber}

SELECT ID, PRODUCT_NUMBER, ORIGINAL_NAME, FILE_PATH, FILE_TYPE, UPLOAD_DATE
FROM PRODUCT_FILE_TB
WHERE PRODUCT_NUMBER = #{productNumber}

SELECT PRODUCT_NUMBER, NAME, DESCRIPTION, PRICE, QUANTITY, CREATED_DATE
FROM PRODUCT_TB
WHERE PRODUCT_NUMBER = #{arg0}
